/*****触发器：删除模块后自动删除子模块*****/                                                                                               

CREATE TRIGGER SYS_Module_Delete ON SYS_Module AFTER DELETE AS

BEGIN
SET NOCOUNT ON

DELETE M from Deleted D
  join SYS_Module M on M.ParentId = D.ID

END
GO

/*****触发器：增加合并记录后检查已合并记录，如有该节点已经被合并，自动合并到现节点*****/

CREATE TRIGGER SYS_OrgMerger_Insert ON SYS_OrgMerger AFTER INSERT AS

BEGIN
SET NOCOUNT ON

UPDATE O set Validity = 0 from Inserted I
  join SYS_Organization O on O.ID = I.MergerOrgId

UPDATE OM set OrgId = I.OrgId from Inserted I
  join SYS_OrgMerger OM on OM.OrgId = I.MergerOrgId

END
GO

/*****触发器：删除组织机构后自动更新被合并节点状态为有效*****/

CREATE TRIGGER SYS_OrgMerger_Delete ON SYS_OrgMerger AFTER DELETE AS

BEGIN
SET NOCOUNT ON

UPDATE O set Validity = 1 from Deleted D
  join SYS_Organization O on O.ID = D.MergerOrgId

END
GO

/*****触发器：自动添加用户组【所有用户】成员*****/

CREATE TRIGGER SYS_User_Insert ON SYS_User AFTER INSERT AS

BEGIN
SET NOCOUNT ON

DECLARE @GroupId UNIQUEIDENTIFIER
select @GroupId = ID from SYS_UserGroup where Name = 'AllUsers'

INSERT SYS_UserGroupMember (GroupId, UserId, CreatorUserId)
select @GroupId, TI.ID, TI.CreatorUserId 
from Inserted TI 
where TI.Type > 0

END
GO
